var lat1 = flow.get("location").latitude;
var lon1 = flow.get("location").longitude;
var lat2 = msg.payload.latitude;
var lon2 = msg.payload.longitude;

/* calculate distance as specified in 
   https://www.movable-type.co.uk/scripts/latlong.html */

const R = 6371e3; // metres
const φ1 = lat1 * Math.PI/180; // φ, λ in radians
const φ2 = lat2 * Math.PI/180;
const Δφ = (lat2-lat1) * Math.PI/180;
const Δλ = (lon2-lon1) * Math.PI/180;

const a = Math.sin(Δφ/2) * Math.sin(Δφ/2) +
          Math.cos(φ1) * Math.cos(φ2) *
          Math.sin(Δλ/2) * Math.sin(Δλ/2);
const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

const d = R * c; // in metres

msg.payload.distance = Math.round(d / 1000) ;

return msg;
